// Update each product
let updatedCount = 0;
for \(const product of products\) {
  const mapping = mappings[product.id];
  if \(mapping\) {
    product.swatch = mapping.swatch;
    product.images = mapping.images;
    updatedCount++;
    console.log\(`Updated product ${product.id}: ${product.name}`\);
  } else {
    console.warn\(`WARNING: No mapping found for product ${product.id}: ${product.name}`\);
  }
}

// Write the updated JSON back to file
fs.writeFileSync\(filePath, JSON.stringify\(products, null, 2\) + '\\n', 'utf8'\);

console.log\(`\\nDone! Updated ${updatedCount} of ${products.length} products.`\);
